void packet_handlers_fill_capability(struct packet_handlers *phandlers,
                                     const char *capability)
{
  if (is_server()) {
    if (has_capability("techloss_forgiveness", capability)) {
      log_packet_detailed("PACKET_GAME_INFO: using variant=100 cap=%s", capability);
      phandlers->send[PACKET_GAME_INFO].packet = (int(*)(struct connection *, const void *)) send_packet_game_info_100;
    } else if (!has_capability("techloss_forgiveness", capability)) {
      log_packet_detailed("PACKET_GAME_INFO: using variant=101 cap=%s", capability);
      phandlers->send[PACKET_GAME_INFO].packet = (int(*)(struct connection *, const void *)) send_packet_game_info_101;
    } else {
      log_error("Unknown PACKET_GAME_INFO variant for cap %s", capability);
    }
  } else {
    if (has_capability("techloss_forgiveness", capability)) {
      log_packet_detailed("PACKET_GAME_INFO: using variant=100 cap=%s", capability);
      phandlers->receive[PACKET_GAME_INFO] = (void *(*)(struct connection *)) receive_packet_game_info_100;
    } else if (!has_capability("techloss_forgiveness", capability)) {
      log_packet_detailed("PACKET_GAME_INFO: using variant=101 cap=%s", capability);
      phandlers->receive[PACKET_GAME_INFO] = (void *(*)(struct connection *)) receive_packet_game_info_101;
    } else {
      log_error("Unknown PACKET_GAME_INFO variant for cap %s", capability);
    }
  }
}
